import React from 'react';
import { Spotify } from 'react-spotify-embed'
import './radio.css';

const Radio = ({ url }) => {
    const fullURL = new URL(url);
    return (
        <div>
            <h1>Spotify Player</h1>
            <Spotify link={`https://open.spotify.com/track/${fullURL.pathname}`} />
        </div>
    );
}

export default Radio;

