            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        });

        if (response.ok) {
            const responseData = await response.json();
            console.log(responseData)

            navigate('/radio', { state: { url: responseData.url} });
        } else {
            alert("Error occurred while fetching data");
        }
    };

