import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

function App() {
    const navigate = useNavigate();

    const [url, setUrl] = useState('');
    const [song, setSong] = useState('');
    const [artist, setArtist] = useState('');
    const [isReadyToNavigate, setIsReadyToNavigate] = useState(false);

    useEffect(() => {
        // Reset the input values when the component mounts (user navigates back)
        return () => {
            setUrl('');
            setSong('');
            setArtist('');
        };
    }, []);

    const Grab = async (e) => {
        e.preventDefault()

        const data = {
            song: song,
            artist: artist
        };

        const response = await fetch('/start', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        });

        if (response.ok) {
            const responseData = await response.json();
            console.log(responseData)

            // Simulate an asynchronous update to valueToPass
            useEffect(() => {
                // Emulate a delay, such as an API call or user interaction
                const delay = setTimeout(() => {
                setUrl(responseData.url);
                setIsReadyToNavigate(true);
                }, 2000); // Adjust the delay as needed

                // Clear the timeout in case the component unmounts
                return () => clearTimeout(delay);
            }, []);

            useEffect(() => {
                if (isReadyToNavigate) {
                    navigate('/radio', { state: { url: url} });
                }
