        } else {
            alert('Error occurred while fetching data');
        }
    };

    return (
        <div className="App aurora-outer">
            <section id="up" />
            <section id="down" />
            <section id="left" />
            <section id="right" />
            <div className="box stuff">
                <h1>Borealis</h1>
                <form onSubmit={Grab}>
                    <input
                        type="text"
                        value={song}
                        placeholder='Song'
                        onChange={(e) => setSong(e.target.value)}
                    />
                    <input
                        type="text"
                        value={artist}
                        placeholder='Artist'
                        onChange={(e) => setArtist(e.target.value)}
                    />
                    <button type="submit">Play</button>
                </form>
            </div>
        </div> 
    );
}

export default App;
