import axios from 'axios';
import './App.css';

class Base extends React.Component {
  constructor(props) {
    super(props);
    this.state = {song: "", artist: ""};
  }

  grab() {
    e.preventDefault()
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ song: 'React POST Request Example' })
    };

    fetch('http://127.0.0.1:5000/play', requestOptions)
    .then(function(response) {
      console.log(response)
      return response.json()
    })

    fetch('/play', {
        method: "POST",
        body: JSON.stringify({
            "song": state['song'],
            "artist": state['artist'],
        })
    })
  }

  render() {
    return (
      <div className="wrapper">
        <h1>Test</h1>
        <form onSubmit={grab}>
            <p>
              <label>Song</label>
              <input type="text" name="song" onChange={(e) => this.setState({ song: e.target.value })}/>
              <label>Artist</label>
              <input type="text" name="artist" onChange={(e) => this.setState({ artist: e.target.value })}/>
          </p>
          <p>
              <input type="submit" value="Play" />
          </p>
        </form>

      </div>        
    );
  }
}

export default App;
