import React from 'react';
import { Spotify } from 'react-spotify-embed'
// import { useNavigate, useLocation } from 'react-router-dom'
import './radio.css';

const Radio = () => {
    const location = useLocation();
    const url = location.state;

    return (
        <div className='Radio'>
            <h1>Now Playing...</h1>
            <Spotify link={url} />
        </div>
    );
}

export default Radio;


