import React from 'react';
import './App.css';
import { createRoot } from 'react-dom/client';
import Spotify from 'react-spotify-embed/dist/package.json'
import { Container } from 'react-bootstrap'
import { BrowserRouter as Router, Switch, Routes, Route, Link } from 'react-router-dom';
import Radio from './radio';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {song: "", artist: ""};
  }

  grab = async (e) => {
    e.preventDefault()
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        "song": this.state['song'],
        "artist": this.state['artist'],
      })
    };

    fetch('/start', requestOptions)
    .then(function(response) {
      console.log(response.json())
      // const url = new URL(response['url']);
      return (
        <Switch>
          <Route path="/radio" element={<Radio />} />
        </Switch>
      );
    })
  }

  render() {
    return (
      <div className="wrapper">
        <h1>Test</h1>
        <form onSubmit={this.grab}>
            <p>
              <label>Song</label>
              <input type="text" name="song" onChange={(e) => this.setState({ song: e.target.value })}/>
              <label>Artist</label>
              <input type="text" name="artist" onChange={(e) => this.setState({ artist: e.target.value })}/>
          </p>
          <p>
              <Link to="/radio">
                <input type="submit" value="Play" />
              </Link>
          </p>
        </form>

      </div>        
    );
  }
}

export default App;
