import React from 'react';
import { Container } from 'react-bootstrap'
import { useNavigate } from 'react-router-dom'

const Radio = ({ url }) => {
    const navigate = useNavigate();
    if (url == '') { navigate('/'); }

    const fullURL = new URL(url);
    return (
        <div>
            <Container>
                <h1>Iframe Demo</h1>
                <iframe autoPlay title="Spotify Web Player" src={`https://open.spotify.com/embed${fullURL.pathname}`} width={300} height={380} allow="encrypted-media" style={{borderRadius: 8}}/>
            </Container>
        </div>
    );
}

export default Radio;

